import {get, del, post, put, upload} from "./HttpService";
import endpoints from "../config/endpoints";

export default class ModuleService {
    static search(dataSearch = {nextToken: "", limit: 10, query: {}}) {
        return get(endpoints.moduleManagement.list, dataSearch).then(value => {
            return value ? value.data : null;
        });
    }

    static remove(id) {
        return del(endpoints.moduleManagement.delete.replace(":ID", id)).then(value => {
            return value ? value.data : null;
        });
    }

    static get(id) {
        return get(endpoints.moduleManagement.get.replace(":ID", id)).then(value => {
            return value ? value.data : null;
        });
    }

    static update(id, item) {
        return put(endpoints.moduleManagement.update.replace(":ID", id), item).then(value => {
            return value ? value.data : null;
        });
    }

    static updateSerial(id, items) {
        return put(endpoints.moduleManagement.updateSerial.replace(":ID", id), items).then(value => {
            return value ? value.data : null;
        });
    }

    static updateKey(item) {
        return put(endpoints.moduleManagement.updateKey, item).then(value => {
            return value ? value.data : null;
        });
    }

    static createKey(item) {
        return post(endpoints.moduleManagement.updateKey, item).then(value => {
            return value ? value.data : null;
        });
    }

    static clone(id, linkage = false, cloneItems = false, surfix = '') {
        return post(endpoints.moduleManagement.clone.replace(':ID', id), {
            linkage: linkage,
            cloneItems,
            surfix: surfix
        }).then(value => {
            return value ? value.data : null;
        });
    }

    static create(item) {
        return post(endpoints.moduleManagement.create, item).then(value => {
            return value ? value.data : null;
        });
    }

    static upload(file) {
        var formdata = new FormData();
        formdata.append("file", file);
        return upload(endpoints.moduleManagement.upload, formdata).then(value => {
            return value ? value.data : null;
        });
    }
}
