import {Col, Row} from "react-bootstrap";
import {Button, Form, Input, InputNumber, Select} from "antd";
import {MinusCircleOutlined, PlusOutlined} from "@ant-design/icons";
import React, {useEffect, useState} from "react";
import ExerciseService from "../../services/ExerciseService";

export function AddSteps(props) {
  console.log(props.defaultValue);
  const [fs, setFs] = useState(props.value ? props.value : []);
  const [exs, setExs] = useState([]);

  useEffect(() => {
    ExerciseService.searchExercise({all: true}).then(value => {
      if (value) {
        setExs(value.data.items.map(x => {
          return {
            name: x.name,
            id: x.id
          }
        }));
      }
    })
  }, []);

  useEffect(() => {
    props.onChange(fs);
  }, [fs]);

  return (
    <Row style={{marginBottom: 20}}>
      <Col>
        <label className={'eme-title'}>Exercises</label>
        {/*<button type={'button'} className={'eme-b'} onClick={() => {*/}
        {/*  setFs([...fs, {type: 'Exercise list'}])*/}
        {/*}}>Add*/}
        {/*</button>*/}

        {
          fs.map((f, index) => {
            return (
              <div className={'eme'} key={index.toString()}>
                <div key={`name-${index}`}>
                  <Input onChange={event => {
                    const fsCache = [...fs];
                    fsCache[index].type = event.target.value;
                    setFs(fsCache);
                  }} value={f.type} style={{marginBottom: 10}} defaultValue={f.type}/>
                </div>
                <div className={'eme-list'}>
                  <div className={'eme-b'} key={index.toString()}>
                    <div key={`name3-${index}`}>
                      <label>Exercise {index + 1}</label>
                      <Select
                        showSearch
                        optionFilterProp="children"
                        filterOption={(input, option) =>
                          option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                        onChange={value1 => {
                        const fsCache = [...fs];
                        fsCache[index].exerciseId = value1;
                        setFs(fsCache);
                      }} value={f.exerciseId} defaultValue={f.exerciseId} allowClear
                              style={{width: '100%'}}>
                        {
                          exs.map((value1, index2) => {
                            return (
                              <Select.Option value={value1.id} key={index2}>{value1.name}</Select.Option>
                            )
                          })
                        }
                      </Select>
                    </div>
                    <div key={`name2-${index}`}>
                      <label>Rest time {index}</label>
                      <InputNumber onChange={value1 => {
                        const fsCache = [...fs];
                        fsCache[index].restTime = value1;
                        setFs(fsCache);
                      }} value={f.restTime} defaultValue={f.restTime} placeholder={"Input rest time"}
                                   style={{width: '100%'}}/>
                    </div>
                    <div key={`Durati4on-${index}`}>
                      <label>Set</label>
                      <InputNumber onChange={value1 => {
                        const fsCache = [...fs];
                        fsCache[index].set = value1;
                        setFs(fsCache);
                      }} value={f.set} defaultValue={f.set} style={{width: '100%'}}/>
                    </div>
                    <div key={`Duration-${index}`}>
                      <label>Duration</label>
                      <InputNumber onChange={value1 => {
                        const fsCache = [...fs];
                        fsCache[index].durations = value1;
                        setFs(fsCache);
                      }} value={f.durations} defaultValue={f.durations} style={{width: '100%'}}/>
                    </div>
                    <div key={`name33-${index}`}>
                      <label>Duration Type</label>
                      <Select onChange={value1 => {
                        const fsCache = [...fs];
                        fsCache[index].unitDuration = value1;
                        setFs(fsCache);
                      }} value={f.unitDuration} defaultValue={f.unitDuration} allowClear
                              style={{width: '100%'}}>
                        <Select.Option value={'Seconds'} key={1}>Seconds</Select.Option>
                        <Select.Option value={'rep'} key={2}>Repeat</Select.Option>
                      </Select>
                    </div>

                    <MinusCircleOutlined
                      className="dynamic-delete-button"
                      onClick={() => {
                        let fsCache = [...fs];
                        fsCache = fsCache.filter((x, index2) => index2 !== index);
                        setFs(fsCache);
                      }}
                    />
                  </div>
                </div>
              </div>
            )
          })
        }
        <Button type="dashed" onClick={() => {
          setFs([...fs, {
            type: 'Exercise list',
            restTime: 0,
            set: 1
          }])
        }} block icon={<PlusOutlined/>}>
          Add field
        </Button>
      </Col>
    </Row>
  )
}
