import {get, del, post, put, upload} from "./HttpService";
import endpoints from "../config/endpoints";
import moment from "moment";

const test = {
  data: {
    items: [
      {
        name: '7 Days Flat',
        duration: 34,
        level: 'Basic',
        createdAt: moment(new Date()).subtract(2, "days").toDate().getTime(),
        status: 'Active'
      },
      {
        name: 'Tone out your body',
        duration: 53,
        level: 'Basic',
        createdAt: moment(new Date()).subtract(2, "days").toDate().getTime(),
        status: 'Active'
      },
      {
        name: 'Weight loss',
        duration: 80,
        level: 'Basic',
        createdAt: moment(new Date()).subtract(2, "days").toDate().getTime(),
        status: 'Active'
      },
    ]
  }
}

export default class ProgramService {
  static searchProgram(dataSearch = {nextToken: "", limit: 10, query: {}}) {
    return get(endpoints.programManagement.list, dataSearch).then(value => {
      return value ? value.data : test;
    });
  }

  static getProgram(id) {
    return get(endpoints.programManagement.get.replace(":ID", id)).then(value => {
      return value ? value.data : null;
    });
  }

  static removeProgram(id) {
    return del(endpoints.programManagement.delete.replace(":ID", id)).then(value => {
      return value ? value.data : null;
    });
  }

  static updateProgram(id, program) {
    return put(endpoints.programManagement.update.replace(":ID", id), program).then(value => {
      return value ? value.data : null;
    });
  }

  static createProgram(program) {
    return post(endpoints.programManagement.create, program).then(value => {
      return value ? value.data : null;
    });
  }

  static listUserInProgram(programId, group, params) {
    return get(endpoints.programManagement.listUser.replace(":ID", programId), {...params, group}).then(value => {
      return value ? value.data : null;
    });
  }

  static addUserToProgram(programId, username) {
    return put(endpoints.programManagement.addUser.replace(":ID", programId), {user: username}).then(value => {
      return value ? value.data : null;
    });
  }

  static uploadLogoProgram(file) {
    var formdata = new FormData();
    formdata.append("file", file);
    return upload(endpoints.programManagement.upload, formdata).then(value => {
      return value ? value.data : null;
    });
  }

  static removeUserFromProgram(programId, username) {
    return put(endpoints.programManagement.removeUser.replace(":ID", programId), {user: username}).then(value => {
      return value ? value.data : null;
    });
  }
}
