                                            <PhoneInput
                                                international={true}
                                                defaultCountry={'SG'}
                                                placeholder="Enter phone number"
                                                value={phone}
                                                className="form-control"
                                                onChange={(v) => {
                                                    console.log(v);
                                                    if (!v) {
                                                        setPhone(v + '');
                                                    } else {
                                                        setPhone(v);
                                                    }

                                                }}/>
                                            {/*<Form.Control*/}
                                            {/*  maxLength={255}*/}
                                            {/*  placeholder="Enter user phone"*/}
                                            {/*  value={dataAdd.phone_number || ""}*/}
                                            {/*  onChange={(e) => onChangeValue("phone_number", e.target.value)}*/}
                                            {/*/>*/}
                                        </Form.Group>
                                    </Form.Row>
                                    <Form.Row>
                                        <Form.Group as={Col} controlId="formBasicName">
                                            <Form.Label>Address</Form.Label>
                                            <Form.Control
                                                type="text"
                                                maxLength={255}
                                                placeholder="Enter address"
                                                value={dataAdd.address || ""}
                                                onChange={(e) => onChangeValue("address", e.target.value)}
                                                onBlur={(e) => onChangeValue("address", e.target.value.trim())}
                                            />
                                        </Form.Group>
                                        <Form.Group as={Col}>
                                            <Form.Label>Birthday </Form.Label>{" "}
