                                                <DatePicker format={'DD/MM/YYYY'} style={{width: '100%'}}
                                                            placeholder={'Select birthday'}
                                                            type={'date'}
                                                            autocomplete={'new-password'} allowClear
                                                            name={'dateinput'}
                                                            value={dataAdd.birthday ? moment(dataAdd.birthday, 'YYYY-MM-DD') : null}
                                                            onChange={(e) => onChangeValue("birthday", e.format('YYYY-MM-DD'))}/>
                                            </div>

                                        </Form.Group>
                                    </Form.Row>
                                    <Form.Row>
                                        <Form.Group as={Col} controlId="formBasicName">
                                            <Form.Label>Gender</Form.Label>
                                            <SelectForm
                                                optionData={GENDER}
                                                keyString="id"
                                                labelString="name"
                                                value={dataAdd.gender || ""}
                                                onChangeValue={(value) => {
                                                    onChangeValue("gender", value);
                                                }}
                                            />
                                        </Form.Group>
                                        <Form.Group as={Col}>
                                            <Form.Label className="starDanger">Group </Form.Label>{" "}
                                            <SelectForm
                                                optionData={ROLES}
                                                keyString="id"
                                                labelString="name"
                                                value={dataAdd.group || ""}
                                                disabled={true}
                                                onChangeValue={(value) => {
                                                    onChangeValue("group", value);
                                                }}
                                            />
                                        </Form.Group>

                                    </Form.Row>
                                    <Form.Row>
                                        <Form.Group as={Col} controlId="formBasicName">
                                            <Form.Label>Password (<span onClick={() => setPasswordAuto()}
                                                                        className="gen-pass">Generate secure passwords</span>)</Form.Label>
                                            <Form.Control
                                                ref={passwordRef}
                                                type="password"
                                                maxLength={255}
                                                autoFocus
                                                placeholder="Enter user password"
                                                value={dataAdd.password || ""}
